	(32, 1, '2013-01-09 17:53:32', '2013-01-09 17:53:32', '', 'Windows-8-logo-300x300', '', 'inherit', 'open', 'open', '', 'windows-8-logo-300x300-2', '', '', '2013-01-09 17:53:32', '2013-01-09 17:53:32', '', 28, 'http://localhost/thepdahoi.vn/wp-content/uploads/2013/01/Windows-8-logo-300x3001.jpg', 0, 'attachment', 'image/jpeg', 0),
